    private void readPrintBaseActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_readPrintBaseActionPerformed
        // TODO add your handling code here:
        method.printBasePlayers(data, answersArea, mainTable);
    }//GEN-LAST:event_readPrintBaseActionPerformed
    
