    /**
     * Get averages button action
     * @param evt 
     */
    private void getAveragesActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_getAveragesActionPerformed
        // TODO add your handling code here:
        answersArea.append("Found the average height and age of players!\n");
        method.printAverages(answersArea);
    }//GEN-LAST:event_getAveragesActionPerformed
    
    /**
     * Format button action
     * @param evt 
     */
    private void formatActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_formatActionPerformed
        // TODO add your handling code here:
        method.formattingTallest();
        answersArea.append("Formed a new list with players who are taller than the average height!\n");
        method.updateTable(mainTable);
    }//GEN-LAST:event_formatActionPerformed

    /**
     * Sort button action
     * @param evt 
     */
    private void sortActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_sortActionPerformed
        // TODO add your handling code here:
        method.sortPlayersByAgeAndName();
        answersArea.append("Players were sorted by age in ascending order and name alphabetically!\n");
        method.updateTable(mainTable);
    }//GEN-LAST:event_sortActionPerformed
    
    /**
     * Remove button action
     * @param evt 
     */
    private void removeActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_removeActionPerformed
        // TODO add your handling code here:
        method.removePlayers(age);
        answersArea.append("Removed players who are older than the entered age!");
        method.updateTable(mainTable);
    }//GEN-LAST:event_removeActionPerformed
    
